/**
 * Expose `plugin`
 */
module.exports = plugin;

/**
 * Metalsmith plugin to add extensions by mime type.
 *
 * @return {Function}
 */
function plugin() {
	return function(files, metalsmith, done) {
		setImmediate(done);

		Object.keys(files).forEach(function(file) {
			var contentType = files[file]["mime"];

			if (contentType && contentType == "text/html") {
				var newFile = file;

				newFile += ".html";

				files[newFile] = files[file];

				delete files[file];
			}
		});
	}
}